import React from "react";
import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  Dimensions,
} from "react-native";
import { Button } from "native-base";
import { FontAwesome } from "@expo/vector-icons";
//get array of length 25
var itemArray = new Array(25).fill("empty");

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      randomnumber: "",
    };
  }

  //when app load up
  componentDidMount() {
    //TODO: call generate random number method
    this.setState({ randomnumber: this.generateRandomNumber() });
  }

  generateRandomNumber = () => {
    //TODO: generate random number
    let randomnumber = Math.floor(Math.random() * 25);
    this.setState({ randomnumber: randomnumber, isScrached: true });
    return randomnumber;
  };

  scratchItem = (itemNumber) => {
    //decide lucky or unlucky
    if (this.state.randomnumber === itemNumber) {
      itemArray[itemNumber] = "lucky";
    } else {
      itemArray[itemNumber] = "unlucky";
    }
    this.forceUpdate();
  };

  scratchItemIcon = (itemNumber) => {
    //find right icon
    if (itemArray[itemNumber] === "lucky") {
      return "dollar";
    } else if (itemArray[itemNumber] === "unlucky") {
      return "flown-o";
    }
    return "circle";
  };

  scratchItemColor = () => {
    //find right color
    if (itemArray[itemNumber] === "lucky") {
      return "green";
    } else if (itemArray[itemNumber] === "unlucky") {
      return "red";
    }
    return "black";
  };
  showAllItem = () => {
    //decide lucky number or unlucky
    itemArray.fill("unlucky");
    itemArray[this.state.randomnumber] = "lucky";
    this.forceUpdate();
  };
  resetGame = () => {
    //button reset the game
    this.setState({ randomnumber: this.generateRandomNumber() }, () => {
      itemArray.fill("empty");
      this.forceUpdate;
    });
  };

  render() {
    return (
      <View style={styles.container}>
        <Text>Scratch and Win Game </Text>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});
