import React from "react";
import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  Dimensions,
} from "react-native";
import { Button } from "native-base";
import { FontAwesome } from "@expo/vector-icons";
//get array of length 25
var itemArray = new Array(25).fill("empty");

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      randomnumber: "",
    };
  }

  //when app load up
  componentDidMount() {
    //TODO: call generate random number method
    this.setState({ randomnumber: this.generateRandomNumber() });
  }

  generateRandomNumber = () => {
    //TODO: generate random number
    let randomnumber = Math.floor(Math.random() * 25);
    this.setState({ randomnumber: randomnumber, isScrached: true });
    return randomnumber;
  };

  scratchItem = (itemNumber) => {
    //decide lucky or unlucky
    if (this.state.randomnumber === itemNumber) {
      itemArray[itemNumber] = "lucky";
    } else {
      itemArray[itemNumber] = "unlucky";
    }
    this.forceUpdate();
  };

  scratchItemIcon = (itemNumber) => {
    //find right icon
    if (itemArray[itemNumber] === "lucky") {
      return "dollar";
    } else if (itemArray[itemNumber] === "unlucky") {
      return "frown-o";
    }
    return "circle";
  };

  scratchItemColor = (itemNumber) => {
    //find right color
    if (itemArray[itemNumber] === "lucky") {
      return "green";
    } else if (itemArray[itemNumber] === "unlucky") {
      return "red";
    }
    return "blue";
  };
  showAllItem = () => {
    //decide lucky number or unlucky
    itemArray.fill("unlucky");
    itemArray[this.state.randomnumber] = "lucky";
    this.forceUpdate();
  };
  resetGame = () => {
    //button reset the game
    this.setState({ randomnumber: this.generateRandomNumber() }, () => {
      itemArray.fill("empty");
      this.forceUpdate;
    });
  };

  render() {
    return (
      <View style={styles.container}>
        <View>
          <Text>Scratch and Win Game </Text>
        </View>
        <View style={styles.grid}>
          <View style={styles.itemrow}>
            <TouchableOpacity
              style={styles.item} //styles
              onPress={() => {
                this.scratchItem(0);
              }}
            >
              <FontAwesome
                name={this.scratchItemIcon(0)}
                size={50}
                color={this.scratchItemColor(0)}
              />
            </TouchableOpacity>

            <TouchableOpacity
              style={styles.item} //styles
              onPress={() => {
                this.scratchItem(1);
              }}
            >
              <FontAwesome
                name={this.scratchItemIcon(1)}
                size={50}
                color={this.scratchItemColor(1)}
              />
            </TouchableOpacity>

            <TouchableOpacity
              style={styles.item} //styles
              onPress={() => {
                this.scratchItem(2);
              }}
            >
              <FontAwesome
                name={this.scratchItemIcon(2)}
                size={50}
                color={this.scratchItemColor(2)}
              />
            </TouchableOpacity>

            <TouchableOpacity
              style={styles.item} //styles
              onPress={() => {
                this.scratchItem(3);
              }}
            >
              <FontAwesome
                name={this.scratchItemIcon(3)}
                size={50}
                color={this.scratchItemColor(3)}
              />
            </TouchableOpacity>

            <TouchableOpacity
              style={styles.item} //styles
              onPress={() => {
                this.scratchItem(4);
              }}
            >
              <FontAwesome
                name={this.scratchItemIcon(4)}
                size={50}
                color={this.scratchItemColor(4)}
              />
            </TouchableOpacity>
          </View>
          <View style={styles.itemrow}>
            <TouchableOpacity
              style={styles.item} //styles
              onPress={() => {
                this.scratchItem(0);
              }}
            >
              <FontAwesome
                name={this.scratchItemIcon(0)}
                size={50}
                color={this.scratchItemColor(0)}
              />
            </TouchableOpacity>

            <TouchableOpacity
              style={styles.item} //styles
              onPress={() => {
                this.scratchItem(1);
              }}
            >
              <FontAwesome
                name={this.scratchItemIcon(1)}
                size={50}
                color={this.scratchItemColor(1)}
              />
            </TouchableOpacity>

            <TouchableOpacity
              style={styles.item} //styles
              onPress={() => {
                this.scratchItem(2);
              }}
            >
              <FontAwesome
                name={this.scratchItemIcon(2)}
                size={50}
                color={this.scratchItemColor(2)}
              />
            </TouchableOpacity>

            <TouchableOpacity
              style={styles.item} //styles
              onPress={() => {
                this.scratchItem(3);
              }}
            >
              <FontAwesome
                name={this.scratchItemIcon(3)}
                size={50}
                color={this.scratchItemColor(3)}
              />
            </TouchableOpacity>

            <TouchableOpacity
              style={styles.item} //styles
              onPress={() => {
                this.scratchItem(4);
              }}
            >
              <FontAwesome
                name={this.scratchItemIcon(4)}
                size={50}
                color={this.scratchItemColor(4)}
              />
            </TouchableOpacity>
          </View>
          <View style={styles.itemrow}>
            <TouchableOpacity
              style={styles.item} //styles
              onPress={() => {
                this.scratchItem(0);
              }}
            >
              <FontAwesome
                name={this.scratchItemIcon(0)}
                size={50}
